import React, { Fragment, useEffect, useContext, useState } from "react";
import OrderSuccessMessage from "./OrderSuccessMessage";
import { HomeContext } from "./";
import { sliderImages } from "../../admin/dashboardAdmin/Action";
import { prevSlide, nextSlide } from "./Mixins";

const apiURL = process.env.REACT_APP_API_URL;

const Slider = (props) => {
  const { data, dispatch } = useContext(HomeContext);
  const [slide, setSlide] = useState(0);

  useEffect(() => {
    sliderImages(dispatch);
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, []);

  // Auto-advance slides every 4 seconds
  useEffect(() => {
    if (!data?.sliderImages || data.sliderImages.length === 0) return;
    const intervalId = setInterval(() => {
      nextSlide(data.sliderImages.length, slide, setSlide);
    }, 4000);
    return () => clearInterval(intervalId);
  }, [data?.sliderImages, slide]);

  return (
    <Fragment>
      <div className="relative mt-16 bg-gray-100 border-2 h-56 overflow-hidden">
        {data.sliderImages.length > 0 ? (
          <img
            className="w-full h-full object-cover"
            src={`${apiURL}/uploads/customize/${data.sliderImages[slide].slideImage}`}
            alt="sliderImage"
          />
        ) : (
          ""
        )}

        {/* Always render controls; disable when no images */}
        <>
          <button
            type="button"
            aria-label="Previous slide"
            onClick={(e) => {
              if (data?.sliderImages?.length > 0) {
                prevSlide(data.sliderImages.length, slide, setSlide);
              }
            }}
            aria-disabled={!data?.sliderImages?.length}
            className={`z-20 absolute left-4 top-1/2 -translate-y-1/2 w-10 h-10 md:w-12 md:h-12 rounded-full bg-black text-white flex items-center justify-center shadow ${
              data?.sliderImages?.length ? "cursor-pointer" : "opacity-50 cursor-not-allowed"
            }`}
          >
            <svg
              className={`w-6 h-6 md:w-7 md:h-7`}
              fill="none"
              stroke="currentColor"
              viewBox="0 0 24 24"
              xmlns="http://www.w3.org/2000/svg"
            >
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M15 19l-7-7 7-7" />
            </svg>
          </button>
          <button
            type="button"
            aria-label="Previous slide"
            onClick={(e) => {
              if (data?.sliderImages?.length > 0) {
                prevSlide(data.sliderImages.length, slide, setSlide);
              }
            }}
            aria-disabled={!data?.sliderImages?.length}
            className={`z-20 absolute left-1/2 -translate-x-1/2 top-3 w-10 h-10 md:w-12 md:h-12 rounded-full bg-black text-white flex items-center justify-center shadow ${
              data?.sliderImages?.length ? "cursor-pointer" : "opacity-50 cursor-not-allowed"
            }`}
          >
            <svg
              className={`w-6 h-6 md:w-7 md:h-7`}
              fill="none"
              stroke="currentColor"
              viewBox="0 0 24 24"
              xmlns="http://www.w3.org/2000/svg"
            >
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M15 19l-7-7 7-7" />
            </svg>
          </button>
          <button
            type="button"
            aria-label="Next slide"
            onClick={(e) => {
              if (data?.sliderImages?.length > 0) {
                nextSlide(data.sliderImages.length, slide, setSlide);
              }
            }}
            aria-disabled={!data?.sliderImages?.length}
            className={`z-20 absolute right-4 top-1/2 -translate-y-1/2 w-10 h-10 md:w-12 md:h-12 rounded-full bg-black text-white flex items-center justify-center shadow ${
              data?.sliderImages?.length ? "cursor-pointer" : "opacity-50 cursor-not-allowed"
            }`}
          >
            <svg
              className={`w-6 h-6 md:w-7 md:h-7`}
              fill="none"
              stroke="currentColor"
              viewBox="0 0 24 24"
              xmlns="http://www.w3.org/2000/svg"
            >
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M9 5l7 7-7 7" />
            </svg>
          </button>
          <button
            type="button"
            aria-label="Next slide"
            onClick={(e) => {
              if (data?.sliderImages?.length > 0) {
                nextSlide(data.sliderImages.length, slide, setSlide);
              }
            }}
            aria-disabled={!data?.sliderImages?.length}
            className={`z-20 absolute left-1/2 -translate-x-1/2 bottom-3 w-10 h-10 md:w-12 md:h-12 rounded-full bg-black text-white flex items-center justify-center shadow ${
              data?.sliderImages?.length ? "cursor-pointer" : "opacity-50 cursor-not-allowed"
            }`}
          >
            <svg
              className={`w-6 h-6 md:w-7 md:h-7`}
              fill="none"
              stroke="currentColor"
              viewBox="0 0 24 24"
              xmlns="http://www.w3.org/2000/svg"
            >
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M9 5l7 7-7 7" />
            </svg>
          </button>
        </>
      </div>
      <OrderSuccessMessage />
    </Fragment>
  );
};

export default Slider;
