import React, { Fragment, useState, useEffect } from "react";
import { useHistory } from "react-router-dom";
import AdminLayout from "../layout";
import { sendOtp, verifyOtp } from "./FetchApi";

const PhoneVerifyComponent = () => {
  const history = useHistory();
  const [hospitalInfo, setHospitalInfo] = useState(null);
  const [state, setState] = useState({ 
    otp: "", 
    message: "", 
    sending: false, 
    verifying: false, 
    phoneVerified: false,
    otpSent: false,
    error: null
  });

  useEffect(() => {
    // Get hospital information from localStorage
    const currentUser = JSON.parse(localStorage.getItem("jwt"));
    if (currentUser && currentUser.user) {
      setHospitalInfo(currentUser.user);
    }
  }, []);

  const handleSend = async () => {
    if (!hospitalInfo || !hospitalInfo._id) {
      return setState({ ...state, message: "User not found" });
    }
    setState({ ...state, sending: true, message: "" });
    try {
      const res = await sendOtp({ uId: hospitalInfo._id });
      if (res && res.success) {
        setState({ ...state, sending: false, message: "OTP sent successfully" });
      } else if (res && res.otp) {
        setState({ ...state, sending: false, message: `OTP (dev): ${res.otp}` });
      } else {
        const message = res && res.error ? res.error : "Failed to send OTP";
        setState({ ...state, sending: false, message });
      }
    } catch (err) {
      setState({ ...state, sending: false, message: "Network error sending OTP" });
      console.error("sendOtp error", err);
    }
  };

  const handleVerify = async () => {
    if (!hospitalInfo || !hospitalInfo._id) {
      return setState({ ...state, message: "User not found" });
    }
    if (!state.otp) {
      return setState({ ...state, message: "Please enter OTP" });
    }
    setState({ ...state, verifying: true, message: "" });
    try {
      const res = await verifyOtp({ uId: hospitalInfo._id, otp: state.otp });
      if (res && res.success) {
        // Update localStorage with verified status
        const currentUser = JSON.parse(localStorage.getItem("jwt"));
        if (currentUser && currentUser.user) {
          currentUser.user.phoneVerified = true;
          localStorage.setItem("jwt", JSON.stringify(currentUser));
        }
        setState({ ...state, verifying: false, phoneVerified: true, message: "Phone verified successfully!" });
      } else {
        const message = res && res.error ? res.error : "Verification failed";
        setState({ ...state, verifying: false, message });
      }
    } catch (err) {
      setState({ ...state, verifying: false, message: "Network error verifying OTP" });
      console.error("verifyOtp error", err);
    }
  };

  return (
    <Fragment>
      <div className="m-4 md:m-8">
        <div className="bg-white shadow-lg rounded-lg p-6 max-w-2xl mx-auto">
          <h2 className="text-2xl font-bold text-gray-800 mb-6">Verify Phone Number</h2>

          {hospitalInfo && hospitalInfo.phoneVerified ? (
            <div className="bg-green-50 border border-green-200 rounded-lg p-4 mb-6">
              <div className="flex items-center">
                <svg className="w-6 h-6 text-green-600 mr-2" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M9 12l2 2 4-4m6 2a9 9 0 11-18 0 9 9 0 0118 0z" />
                </svg>
                <span className="text-green-800 font-semibold">Your phone number is already verified!</span>
              </div>
            </div>
          ) : null}

          <div className="mb-6">
            <label className="block text-gray-700 font-semibold mb-2">Phone Number</label>
            <div className="bg-gray-50 border border-gray-300 rounded-lg px-4 py-3 text-gray-800">
              {hospitalInfo && hospitalInfo.phoneNumber ? hospitalInfo.phoneNumber : "No phone number provided"}
            </div>
            {hospitalInfo && !hospitalInfo.phoneNumber ? (
              <p className="text-red-500 text-sm mt-2">Please add a phone number to your profile first.</p>
            ) : null}
          </div>

          {hospitalInfo && hospitalInfo.phoneNumber && !hospitalInfo.phoneVerified ? (
            <>
              <div className="mb-6">
                <button
                  onClick={handleSend}
                  disabled={state.sending}
                  className={`w-full md:w-auto px-6 py-3 rounded-lg text-white font-semibold transition duration-200 ${
                    state.sending
                      ? "bg-gray-400 cursor-not-allowed"
                      : "bg-yellow-600 hover:bg-yellow-700"
                  }`}
                >
                  {state.sending ? "Sending..." : "Send OTP"}
                </button>
              </div>

              <div className="mb-6">
                <label className="block text-gray-700 font-semibold mb-2">Enter OTP</label>
                <input
                  type="text"
                  value={state.otp}
                  onChange={(e) => setState({ ...state, otp: e.target.value })}
                  placeholder="Enter 6-digit OTP"
                  className="w-full border border-gray-300 rounded-lg px-4 py-3 focus:outline-none focus:ring-2 focus:ring-yellow-500"
                />
              </div>

              <div className="mb-6">
                <button
                  onClick={handleVerify}
                  disabled={state.verifying || !state.otp}
                  className={`w-full md:w-auto px-6 py-3 rounded-lg text-white font-semibold transition duration-200 ${
                    state.verifying || !state.otp
                      ? "bg-gray-400 cursor-not-allowed"
                      : "bg-green-600 hover:bg-green-700"
                  }`}
                >
                  {state.verifying ? "Verifying..." : "Verify OTP"}
                </button>
              </div>
            </>
          ) : null}

          {state.message ? (
            <div className={`rounded-lg p-4 ${
              state.message.includes("success") || state.message.includes("verified")
                ? "bg-green-50 border border-green-200 text-green-800"
                : state.message.includes("error") || state.message.includes("failed")
                ? "bg-red-50 border border-red-200 text-red-800"
                : "bg-blue-50 border border-blue-200 text-blue-800"
            }`}>
              {state.message}
            </div>
          ) : null}

          <div className="mt-6 pt-6 border-t border-gray-200">
            <button
              onClick={() => history.push("/admin/dashboard/hospital-details")}
              className="text-gray-600 hover:text-gray-800 font-semibold"
            >
              ← Back to Hospital Details
            </button>
          </div>
        </div>
      </div>
    </Fragment>
  );
};

const PhoneVerify = () => {
  return <AdminLayout children={<PhoneVerifyComponent />} />;
};

export default PhoneVerify;
